CREATE TABLE IF NOT EXISTS "easycard_transactions" ("id" VARCHAR NOT NULL PRIMARY KEY, "terminal_no" VARCHAR, "batch_no" VARCHAR NOT NULL, "message_type" VARCHAR NOT NULL, "transaction_type" VARCHAR NOT NULL, "reference_num" VARCHAR NOT NULL, "order_id" VARCHAR, "amount" INTEGER NOT NULL, "autoload_amount" INTEGER, "details" VARCHAR, "created" INTEGER, "modified" INTEGER, UNIQUE ("batch_no", "transaction_type", "reference_num") ON CONFLICT ABORT);
CREATE INDEX IF NOT EXISTS "easycard_order_txntype" on "easycard_transactions" ("terminal_no", "order_id", "transaction_type");
CREATE INDEX IF NOT EXISTS "easycard_batch_messagetype" on "easycard_transactions" ("terminal_no", "batch_no", "message_type");